jQuery( document ).ready( function () {
    document.addEventListener( 'wpcf7mailsent', function( event ){
        var $formID = event.detail.contactFormId;

        jQuery.ajax({
            url: cccf7.ajaxurl,
            type: 'POST',
            data: {
                action: 'cccf7-form-submit',
                form_id: $formID
            },

            success:function( data ) {

                if( data.data.message == "Do nothing." ){
                    return false;
                }

                window.location = data.data.redirect;
            },

            error: function ( data ) {
                alert( 'Coinbase Commerce Message: '+ data.statusText + ' Code: ' + data.status );
            }
        });
    } );
} )
